create table dms_limbo_msg ( msg_id bigint not null, limbo_id bigint not null, delivery_date bigint not null, delivery_count tinyint not null, msg_src_name varchar(250) not null, port_name varchar(250) not null, msg_class varchar(250) not null, msg_class_type tinyint not null, jms_type varchar(250) null, jms_expiration bigint null, jms_correlationid varchar(250) null ,constraint limbo_msg_pk primary key (msg_id));
create index dms_limbo_read_idx on dms_limbo_msg (limbo_id,delivery_date); -- If serialized reply-to destinations are known to be small enough, this binary column's size can be reduced for efficiency. The size here has been chosen to work with local dms and SQL-JMS.
create table dms_limbo_replyto ( msg_id bigint not null, jms_replyto varbinary(500) null ,constraint limbo_replyto_pk primary key (msg_id));
-- If serialized message bodies are known to be small enough, this binary column's size can be reduced for efficiency. The size here has been chosen to work with almost all types of messages but may be larger than necessary. Actual usage analysis is recommended for finding the optimal binary column size.
create table dms_limbo_body ( msg_id bigint not null, msg_body longblob null ,constraint limbo_body_pk primary key (msg_id));
-- This table represents a map of properties for messages stored above, i.e. there can be multiple rows in this table for a single row in the preceeding tables.
